import { useState } from 'react';
import axios from 'axios'
export default function SupCreateMarkingSchemes() {
  const [formFields, setFormFields] = useState([
    { citerion: '', vgood: '',avg:'',poor:'',mark:'' },
  ])

  const handleFormChange = (event, index) => {
    let data = [...formFields];
    data[index][event.target.name] = event.target.value;
    setFormFields(data);
  }


  async function Submit(e){
    e.preventDefault();
  console.log(formFields)
  const newItem = { ...formFields};

  axios

    .post('http://localhost:5000/markingScheme/add/', formFields)

    .then((res) => console.log(res.data))

}

  const addFields = () => {
    let object = {
      citerion: '', 
      vgood: '',
      avg:'',
      poor:'',
      mark:''
    }

    setFormFields([...formFields, object])
  }

  const removeFields = (index) => {
    let data = [...formFields];
    data.splice(index, 1)
    setFormFields(data)
  }

  return (
    <div className="App">
       <select name="subject" id="subject" value={selectSub}
       onChange={e=>{setFormFields(e.target.val)}}
       
       placeholder="Select the specilaization">



    <option value="" selected="selected">Information technology</option>
    <option value="" selected="selected">Software engineering</option>
    <option value="" selected="selected">Data science</option>
    <option value="" selected="selected">Cyber security</option>
    <option value="" selected="selected">Intractive media</option>
    <option value="" selected="selected">Network engineering</option>

  </select>
  <select name="subject" id="position" placeholder="Select the specilaization">
    <option value="" selected="selected">Panel member</option>
    <option value="" selected="selected">Supervisour</option>
 

  </select>
      <form onSubmit={Submit}>
        {formFields.map((form, index) => {
          return (
            <div key={index}>
              <input
                name='citerion'
                placeholder='Citerion'
                onChange={event => handleFormChange(event, index)}
                value={form.citerion}
              />
              <input
                name='vgood'
                placeholder='Very good'
                onChange={event => handleFormChange(event, index)}
                value={form.vgood}
              />
              <input
                name='avg'
                placeholder='Average'
                onChange={event => handleFormChange(event, index)}
                value={form.avg}
              />
              <input
                name='poor'
                placeholder='Poor'
                onChange={event => handleFormChange(event, index)}
                value={form.poor}
              />
              <input
                name='mark'
                placeholder='Total mark'
                onChange={event => handleFormChange(event, index)}
                value={form.mark}
              />



              <button onClick={() => removeFields(index)}>Remove</button>
            </div>
          )
        })}
      </form>
      <button onClick={addFields}>Add More..</button>
      <br />
      <button onClick={Submit}>Submit</button>
    </div>
  );
}
