import React, {useState,useEffect} from "react";
import NavBar from "../common/navBar";
import axios from 'axios'
import Table from 'react-bootstrap/Table'


export default function ViewMarkingSchemes(){

  const [data,setData]=useState([]);
  const [form,setForm]=useState({
    comment: "",
    mark: ""
  });

  const handleFormChange = (event, index) => {
    let data = [...form];
    data[index][event.target.name] = event.target.value;
    setForm(data);
  }

  
//   async function Submit(e){
//     e.preventDefault();
//   console.log(...form)
  
//   const newItem={...form}
//   axios

//     .post('http://localhost:5000/mark/add', newItem)

//     .then((res) => console.log(res.data))

// }

console.log(form);
console.log("length",data.length)
  const loadData = () => {
    axios
      .get("http://localhost:5000/markingScheme/")
      .then((response) => {
        console.log(response.data);
        console.log(data.length);
        if (data.length == 0) {
          setData(response.data);
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  useEffect(()=>{
    loadData();
  },[]);

    return(

        <div>
            <NavBar/>
<div className="container">
            <Table>
          <tbody>
              <tr>
              <th>Criterion</th>
              <th>Very good</th>
              <th>Average</th>
              <th>Poor</th>
              <th>Mark</th>
              </tr>
              {data.map((markingScheme)=>{
              
              return(
                  <tr>
                      <td>{markingScheme.citerion}</td>
                      <td>{markingScheme.vgood}</td>
                      <td>{markingScheme.avg}</td>
                      <td>{markingScheme.poor}</td>
                      <td>{markingScheme.mark}</td>

                  </tr>

              )
})}
               
              
              </tbody>
            </Table>
           <form>
            <Table striped bordered hover variant="dark">
  <thead>
    <tr>
      
      <th>Citerion</th>
      <th>Comment</th>
      <th>Mark</th>
    </tr>
  </thead>
  <tbody>

    {data.map((markingScheme)=>{
              
              return(
                  <tr>
                      <td>{markingScheme.citerion}</td>
                      form
                      <td><input type='text' value={form.comment} 
                      onChange={(e)=>handleFormChange(e,)}
                      /></td>
      <td><input type='text'
      value={form.mark}
      onChange={(e) => handleFormChange({ mark: e.target.value })}
      /></td>

                  </tr>

              )
})}
   
  
  </tbody>
</Table>
<button >Submit marks</button>
</form>
</div>
        </div>


    )
   
}
