import React, {useState} from "react";
import NavBar from "../common/navBar";
import {Dropdown,Button} from 'react-bootstrap'
import { v4 as uuidv4 } from 'uuid';


export default function SupCreateMarkingSchemes(){
  const [inputFields, setInputFields] = useState([
    { id: uuidv4(), criterion: '', vgood: '',avg:'',poor:'',mark:'' },
  ]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("InputFields", inputFields);
  };

  const handleChangeInput = (id, event) => {
    const newInputFields = inputFields.map(i => {
      if(id === i.id) {
        i[event.target.value] = event.target.value
      }
      return i;
    })
    
    setInputFields(newInputFields);
  }

  const handleAddFields = () => {
    setInputFields([...inputFields, { id: uuidv4(),  criterion: '', vgood: '' ,avg:'',poor:'',mark:'',}])
  }

  const handleRemoveFields = id => {
    const values  = [...inputFields];
    values.splice(values.findIndex(value => value.id === id), 1);
    setInputFields(values);
  }

  return (
  <div>
      <h1>Add New Member</h1>
      <form onSubmit={handleSubmit}>
        { inputFields.map(inputField => (
          <div key={inputField.id}>
            <label>Criterion</label><br/>
            <input
              type='text'
              name="criterion"
              variant="filled"
              value={inputField.criterion}
              onChange={event => handleChangeInput(inputField.id, event)}
            />

            <label>Very good</label><br/>
            <input
              type='text'
              variant="filled"
              value={inputField.vgood}
              onChange={event => handleChangeInput(inputField.id, event)}
            />
            <label>Average</label><br/>
            <input
              type='text'
              variant="filled"
              value={inputField.avg}
              onChange={event => handleChangeInput(inputField.id, event)}
            />
            <label>Poor</label><br/>
            <input
              type='text'
              variant="filled"
              value={inputField.poor}
              onChange={event => handleChangeInput(inputField.id, event)}
            />
           <label>Total mark</label><br/>
            <input
              type='text'
              variant="filled"
              value={inputField.mark}
              onChange={event => handleChangeInput(inputField.id, event)}
            />
            <button disabled={inputFields.length === 1} onClick={() => handleRemoveFields(inputField.id)}>
              
            </button>
            <button
              onClick={handleAddFields}
            >
              
            </button>
          </div>
        )) }
        <Button
        
          variant="contained" 
          color="primary" 
          type="submit" 
       
          onClick={handleSubmit}
        >Send</Button>
      </form>
  </div>
  );
}
