import React, {useState} from "react";
import NavBar from "../common/navBar";
import {Dropdown,Button} from 'react-bootstrap'
import { v4 as uuidv4 } from 'uuid';


export default function SupCreateMarkingSchemes(){

  const [inputFields, setInputFields] = useState([
    { idNo: uuidv4(), citerion: '', vgood: '',avg:'',poor:'',mark:'' },
  ]);



  const handleChangeInput = (id, event) => {
    const newInputFields = inputFields.map(i => {
      if(id === i.idNo) {
        i[event.target.name] = event.target.value
     

      }
    
      return i;
    })
    
    setInputFields(newInputFields);
  }
 
  const handleAddFields = () => {
    setInputFields([...inputFields, { idNo: uuidv4(),citerion: '', vgood: '' ,avg:'',poor:'',mark:''}])
  }

  const handleRemoveFields = id => {
    const values  = [...inputFields];
    values.splice(values.findIndex(value => value.idNo === id), 1);
    setInputFields(values);
  }

  return (
  <div>
      <h1>Add New Member</h1>
      <form onSubmit={handleSubmit}>
        { inputFields.map(inputField => (
          <div key={inputField.idNo}>
            <label>Criterion</label>
            <input
              name="citerion"
              variant="filled"
              value={inputField.citerion}
              onChange={event => handleChangeInput(inputField.idNo, event)}
            />

            <label>Very good</label>
            <input
              name="vgood"
              type='text'
              variant="filled"
              value={inputField.vgood}
              onChange={event => handleChangeInput(inputField.idNo, event)}
            />
            <label>Average</label>
            <input
              type='text'
              name="avg"
              variant="filled"
              value={inputField.avg}
              onChange={event => handleChangeInput(inputField.idNo, event)}
            />
            <label>Poor</label>
            <input
              type='text'
              name="poor"
              variant="filled"
              value={inputField.poor}
              onChange={event => handleChangeInput(inputField.idNo, event)}
            />
           <label>Total mark</label>
            <input
              type='text'
              name="mark"
              variant="filled"
              value={inputField.mark}
              onChange={event => handleChangeInput(inputField.idNo, event)}
            />
            
          </div>
        )) }
        <button disabled={inputFields.length === 1} onClick={() => handleRemoveFields(inputField.id)}>
              
              </button>
              <button
                onClick={handleAddFields}
              >
                
              </button>
        <Button
          type="submit" 
        >Send</Button>
      </form>
  </div>
  );
}
