import React,{useState} from "react";
import NavBar from "../common/navBar";
import Table from 'react-bootstrap/Table'
import {Dropdown,Button} from 'react-bootstrap'
import DropdownButton from 'react-bootstrap/DropdownButton'

export default function PanelCreateMarkingSchemes(){

  const [inputRow,setInputRow]=useState([
    {
      evCiterion:'',
      poor:'',
      avg:'',
      vgood:'',
      mark:''
    }
  ]);

  function handleChangeInput(event) {
    return setInputRow((prev) => {
      return { ...prev, ...event };

    });

  
  }

  // const handleChangeInput = (id, event) => {
  //   const newInputFields = inputFields.map(i => {
  //     if(id === i.id) {
  //       i[event.target.name] = event.target.value
  //     }
  //     return i;
  //   })
  // }
  // This function will handle the submission.
  async function onSubmit(e) {
    e.preventDefault();

    // When a post request is sent to the create url, we'll add a new record to the database.
    const newItem = { ...form };

    await fetch("http://localhost:5000/markingScheme/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newItem),
    }).catch((error) => {
      window.alert(error);
      return;
    });

  

  }


  // const handleAddFields = () => {
  //   setInputRow([...inputRow, {evCiterion: '',poor: '',avg:'',vgood:'',mark:'' }])
  // }

    return(

        <div>
            <NavBar/>

<div className="container">
  <form onSubmit={onSubmit}>
  <h1>Panel Member Marking Scehme</h1>
 <select name="subject" id="subject" placeholder="Select the specilaization">
    <option value="" selected="selected">Information technology</option>
    <option value="" selected="selected">Software engineering</option>
    <option value="" selected="selected">Data science</option>
    <option value="" selected="selected">Cyber security</option>
    <option value="" selected="selected">Intractive media</option>
    <option value="" selected="selected">Network engineering</option>

  </select>
            <Table striped bordered hover variant="dark"
            >
  <thead>
    <tr>
      
      <th>Criterion</th>
      <th>Very good</th>
      <th>Average</th>
      <th>Poor</th>
      <th>Total Mark</th>
    </tr>
  </thead>
  <tbody>
 
    <tr>
       <td><input type='text'
       id="evCiterion"
       value={inputRow.evCiterion}
       onChange={(e)=>handleChangeInput({evCiterion:e.target.value})}  
       /></td>
      <td><textarea type='text'
      id="vgood"
       value={inputRow.vgood}
       onChange={(e)=>({vgood:e.target.value})} 

      /></td>
      <td><textarea type='text'
      id="avg"
       value={inputRow.avg}
       onChange={(e)=>handleChangeInput({avg:e.target.value})} 
      /></td>
         <td><textarea type='text'
         id="poor"
       value={inputRow.poor}
       onChange={(e)=>handleChangeInput(inputRow.poor,event)} 
      /></td>
         <td><input type='number'
       value={inputRow.mark}
       onChange={event=>handleChangeInput(inputRow.mark,event)} 
      /></td>
    </tr>

    <Button
             
            >
              Add row
            </Button>

  </tbody>

</Table>
<button type="submit">Submit</button>
</form>
</div>
        </div>


    )
}
