import React, {useState,useEffect} from "react";
import NavBar from "../common/navBar";
import axios from 'axios'
import Table from 'react-bootstrap/Table'


export default function ViewMarkingSchemes(){

  const [data,setData]=useState([]);
  const [values,setValues]=useState({val:[]});


  
  // function createInputs() {
  //   return values.val.map((el, i) =>
  //     <div key={i}>
  //       <input type="text" value={el||''} onChange={handleChange.bind(i)} />
  //       {/* <input type='button' value='remove' name={i} onClick={removeClick.bind(i)} /> */}
  //     </div>
  //   );
  // }

  function handleChange(event) {
    let vals = [...values.val];
    vals[this] = event.target.value;
    setValues({ val: [...values.val, '']})
  }


  const handleSubmit = event => {
    alert('A name was submitted: ' + values.val.join(', '));
    event.preventDefault();

    
  }

  // console.log(setForm)
  // function updateForm(value) {
  //   return setData((prev) => {
  //     return { ...prev, ...value };
  //   });
  // }

//   async function Submit(e){
//     e.preventDefault();
//   console.log(...form)
  
//   const newItem={...form}
//   axios

//     .post('http://localhost:5000/mark/add', newItem)

//     .then((res) => console.log(res.data))

// }



  const loadData = () => {
    axios
      .get("http://localhost:5000/markingScheme/")
      .then((response) => {
        console.log(response.data);
        console.log(data.length);
        if (data.length == 0) {
          setData(response.data);
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  useEffect(()=>{
    loadData();
  },[]);

    return(

        <div>
            <NavBar/>
<div className="container">
       
            <Table>
          <tbody>
              <tr>
              <th>Criterion</th>
              <th>Very good</th>
              <th>Average</th>
              <th>Poor</th>
              <th>Mark</th>
              </tr>
              {data.map((markingScheme)=>{
              
              return(
                  <tr>
                      <td>{markingScheme.citerion}</td>
                      <td>{markingScheme.vgood}</td>
                      <td>{markingScheme.avg}</td>
                      <td>{markingScheme.poor}</td>
                      <td>{markingScheme.totMark}</td>

                  </tr>

              )
})}
               
              
              </tbody>
            </Table>


            <form onSubmit={handleSubmit}>
            <Table striped bordered hover variant="dark">
  <thead>
    <tr>
      
      <th>Citerion</th>
      <th>Comment</th>
      <th>Mark</th>
    </tr>
  </thead>
  <tbody>

    {data.map((markingScheme)=>{
              
              return(
                  <tr>
                      <td>{markingScheme.citerion}</td>   
                      <td> {function createInputs() {return ((i) =>
      <div key={i}>
        <input type="text" value='' id={i}  onChange={handleChange.bind(i)} />
        {/* <input type='button' value='remove' name={i} onClick={removeClick.bind(i)} /> */}
      </div>)}}</td>
                  </tr>
 
              )
            
})}
    <tr>
      <td>
      <input type='text' value={data.mark} onChange={(e) => setData(e.target.value)}/>
      </td>
    </tr>
   
  
  </tbody>
</Table>
<button >Submit marks</button>
</form>
</div>
        </div>


    )
   
}
