import { useState } from 'react';
import axios from 'axios'
export default function SupCreateMarkingSchemes() {
  const [formFields, setFormFields] = useState([
    { citerion: '', vgood: '',avg:'',poor:'',mark:'' },
  ])

  const handleFormChange = (event, index) => {
    let data = [...formFields];
    data[index][event.target.name] = event.target.value;
    setFormFields(data);
  }


  async function Submit(e){
    e.preventDefault();
  console.log(formFields)
  const newItem = { ...formFields};

  const exercise = {

    citerion: this.state.citerion,

    vgood: this.state.vgood,

    avg: this.state.avg,

    poor: this.state.poor,

    mark: this.state.mark

  }



  console.log(exercise)



  axios

    .post('http://localhost:5000/exercises/add/', formFields)

    .then((res) => console.log(res.data))

}

  const addFields = () => {
    let object = {
      citerion: '', 
      vgood: '',
      avg:'',
      poor:'',
      mark:''
    }

    setFormFields([...formFields, object])
  }

  const removeFields = (index) => {
    let data = [...formFields];
    data.splice(index, 1)
    setFormFields(data)
  }

  return (
    <div className="App">
      <form onSubmit={Submit}>
        {formFields.map((form, index) => {
          return (
            <div key={index}>
              <input
                name='citerion'
                placeholder='Citerion'
                onChange={event => handleFormChange(event, index)}
                value={form.citerion}
              />
              <input
                name='vgood'
                placeholder='Very good'
                onChange={event => handleFormChange(event, index)}
                value={form.vgood}
              />
              <input
                name='avg'
                placeholder='Average'
                onChange={event => handleFormChange(event, index)}
                value={form.avg}
              />
              <input
                name='poor'
                placeholder='Poor'
                onChange={event => handleFormChange(event, index)}
                value={form.poor}
              />
              <input
                name='mark'
                placeholder='Total mark'
                onChange={event => handleFormChange(event, index)}
                value={form.mark}
              />



              <button onClick={() => removeFields(index)}>Remove</button>
            </div>
          )
        })}
      </form>
      <button onClick={addFields}>Add More..</button>
      <br />
      <button onClick={Submit}>Submit</button>
    </div>
  );
}
